mapper.select:
SELECT count(*) FROM board
AND board_Header = #{boardCategory}
AND ( LOWER(board_title) LIKE CONCAT('%', LOWER(#{searchBoard}), '%') OR LOWER(board_content) LIKE CONCAT('%', LOWER(#{searchBoard}), '%') )

SELECT b.board_no AS boardNo, b.board_writer_no AS boardWriterNo, b.board_title AS boardTitle, COUNT(bc.comment_no) AS commentCnt, CASE WHEN b.board_header = 'IN' THEN '정보' WHEN b.board_header = 'SG' THEN '건의사항' WHEN b.board_header = 'DS' THEN '토론' WHEN b.board_header = 'CT' THEN '잡담' WHEN b.board_header = 'QA' THEN '질문' WHEN b.board_header = 'CP' THEN '칭찬' ELSE b.board_header END AS boardHeader, b.board_like AS boardLike, b.board_view AS boardView, DATE_FORMAT(b.create_datetime, '%y-%m-%d') AS createDate FROM board b LEFT JOIN board_comment bc ON b.board_no = bc.board_no
AND b.board_header IN ('IN', 'SG', 'DS', 'CT', 'QA', 'CP')
AND b.board_header = #{boardCategory}
AND (LOWER(b.board_title) LIKE CONCAT('%', LOWER(#{searchBoard}), '%') OR LOWER(b.board_content) LIKE CONCAT('%', LOWER(#{searchBoard}), '%'))
GROUP BY b.board_no ORDER BY boardNo DESC LIMIT #{beginRow}, #{rowPerPage}
