mapper.select:
SELECT COUNT(*) AS existEmp
FROM employee
WHERE emp_no LIKE #{empNo}

SELECT e.emp_no AS empNo , e.emp_name AS empName , CONCAT(ef.file_name, ef.file_ext) AS fileName , e.emp_email AS empEmail , CASE WHEN e.emp_status = 'T' THEN '임시' WHEN e.emp_status = 'E' THEN '재직' WHEN e.emp_status = 'L' THEN '휴직' WHEN e.emp_status = 'R' THEN '퇴직' ELSE '대기' END AS empStatus , di.division_name AS divisionName , di.division_code AS divisionCode , d.dept_name AS deptName , r.rank_name AS rankName , IFNULL(e.is_team_leader,'N') AS isTeamLeader , e.hire_date AS hireDate FROM employee e JOIN department d ON e.dept_code = d.dept_code JOIN division di ON d.division_code = di.division_code JOIN `rank` r ON e.rank_no = r.rank_no LEFT OUTER JOIN employee_file ef ON e.emp_no = ef.emp_no
e.emp_status = 'E'
AND di.division_code = #{divisionCode}
ORDER BY divisionName DESC;

SELECT emp_no  AS empNo
		, emp_name AS empName
		, emp_email AS empEmail
FROM employee
WHERE emp_no LIKE #{empNo}
AND emp_email LIKE #{empEmail}
